import React,{useState, useEffect} from 'react'
import {Form, Button, Table} from 'react-bootstrap'
import {useDispatch, useSelector} from 'react-redux'
import {createBill, fetchBill} from '../../redux/actions/bill/billAction'



function AddBill() {
    const [productKey, setProductKey] = useState(" ")  
    const [quantity, setQuantity] = useState(0)         
   
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(fetchBill())
    }, [dispatch])

    const billList = useSelector(state => state.bill)
    const {salesList, bills} = billList
    console.log(salesList[0][1][0].bills[0].itemName)
    console.log(Object.entries({salesList}))
    // [0][1][0]
    // [0][1][0].bills
    //[0][1][0].bills[0].itemName
    //[0][0]
    
    
   
    // [
    //     {
    //         "_id": "61ab35418166b2b92dc50937",
    //         "productKey": " PRODUCT001",
    //         "quantity": 2,
    //         "__v": 0,
    //         "bill": [
    //             {
    //                 "_id": "61ab20c68f72ed2b2d158ba6",
    //                 "itemName": " Pencil",
    //                 "productKey": " PRODUCT001",
    //                 "unitPrice": 7
    //             }
    //         ],
    //         "id": "61ab35418166b2b92dc50937"
    //     }
    // ]

   

  
    
    const formSubmitHandler = (e) => {
        e.preventDefault();
        dispatch(createBill(productKey, quantity))
        setProductKey(" ")
        setQuantity(0)       
    }


    return (
        <div>
            <Form className = "w-50 mx-auto" onSubmit = {formSubmitHandler} >
                <Form.Group className="mb-2 " controlId="formBasicEmail">
                    <Form.Label>Product Key</Form.Label>
                    <Form.Control                    
                    type="text" 
                    size = "sm" 
                    placeholder="Enter the Product Key"
                    value = {productKey}
                    onChange = {(e) => setProductKey(e.target.value)} />                    
                </Form.Group>               

                <Form.Group className="mb-2 " controlId="formBasicEmail">
                    <Form.Label>Quantity</Form.Label>
                    <Form.Control                   
                    type="number" 
                    size = "sm" 
                    placeholder="Quantity"
                    value = {quantity}
                    onChange = {(e) => setQuantity(e.target.value)} />                    
                </Form.Group>             

                <Button variant="primary" type="submit">
                    Submit
                </Button>
            </Form>   
            <Table className = "w-75 mx-auto" striped bordered hover>
                <thead>
                    <tr>                                          
                        <th>Item Name</th>                        
                        <th>Quantity</th>
                        <th>Unit Price</th>                       
                        <th>Total Amount</th>
                    </tr>
                </thead>
                <tbody>
                     {/* {billDetail && billDetail.map((bill) => {
                        return(
                        <tr key = {bill._id}>              
                            <td>{bill.itemName}</td>                           
                            <td>{bill.quantity}</td>                          
                            <td>{bill.unitPrice}</td>                           
                            <td>{bill.quantity * bill.unitPrice }</td>
                        </tr>
                        )                   
                    })}                      */}
                </tbody>
            </Table>
                    
            
        </div>        
    )
}

export default AddBill
