import axios from 'axios'
import { ADD_SALESITEM_REQUEST, ADD_SALESITEM_SUCCESS, ADD_SALESITEM_FAIL } from '../actionTypes';


// Add Sales item
export const addSalesItem = (_id, quantity) => {
    return async dispatch => {
        try {
            dispatch({
                type: ADD_SALESITEM_REQUEST
            })
            const config = {
                headers: { 'Content-Type': 'application/json' }
            }
            const { data } = await axios.post("http://localhost:5000/api/bill/addsaleitem",
                { _id, quantity },
                config)
            dispatch({
                type: ADD_SALESITEM_SUCCESS,
                payload: data
            })
        }
        catch (error) {
            dispatch({
                type: ADD_SALESITEM_FAIL,
                payload: error.response && error.response.data.message 
            })

        }
    }

}
