import axios from 'axios'
import {
    USER_REGISTER_REQUEST,
    USER_REGISTER_SUCCESS,
    USER_REGISTER_FAIL,
    USER_LOGIN_REQUEST,
    USER_LOGIN_SUCCESS,
    USER_LOGIN_FAIL,
    USER_LOGOUT,
    FETCH_USERS_REQUEST,
    FETCH_USERS_SUCCESS,
    FETCH_USERS_FAIL
} from '../actionTypes';
import { useSelector } from 'react-redux';

// Register User
export const userRegister = (name, email, password) => {
    return async dispatch => {
        try {
            dispatch({
                type: USER_REGISTER_REQUEST
            })
            const config = {
                headers: { 'content-Type': 'application/json' }
            }
            const { data } = await axios.post("http://localhost:5000/api/user/userregister",
                { name, email, password }, config)
            dispatch({
                type: USER_REGISTER_SUCCESS,
                payload: data
            })
            localStorage.setItem('userAuthData', JSON.stringify(data));
        }
        catch (error) {
            dispatch({
                type: USER_REGISTER_FAIL,
                payload: error.response && error.response.data.message
            })
        }
    }
}

export const loginUser = (email, password) => {
    return async dispatch => {
        try {
            dispatch({
                type: USER_LOGIN_REQUEST
            })
            const config = {
                headers: { content_Type: "application/json" }
            }
            const { data } = await axios.post("http://localhost:5000/api/user/login",
                { email, password }, config)
            dispatch({
                type: USER_LOGIN_SUCCESS,
                payload: data
            })
            localStorage.setItem('userAuthData', JSON.stringify(data));
        }
        catch (error) {
            dispatch({
                type: USER_LOGIN_FAIL,
                payload: error.response && error.response.data.message
            })
        }
    }
}

export const logoutUser = () => {
    return async dispatch => {
      localStorage.removeItem('userAuthData');
      try {
        dispatch({
          type: USER_LOGOUT,
        });
      } catch (error) {}
    };
  };


  export const fetchUsersProfile = () => {
    return async (dispatch, getState) => {  
        const {usersInfo} = useSelector(state=>state.user) 
        console.log(usersInfo)   
      try {
        dispatch({
          type: FETCH_USERS_REQUEST,
          loading: true,
        });
        const config = {
          headers: {
            authorization: `Bearer ${usersInfo.token}`
            }
        };
        const { data } = await axios.get('http://localhost:5000/api/user/purchase', config);
        dispatch({
          type: FETCH_USERS_SUCCESS,
          payload: data,
        });
      } catch (error) {
        dispatch({
          type: FETCH_USERS_FAIL,
          error: error.response && error.response.data.message,
        });
      }
    };
  };
