import axios from 'axios'
import { USER_REGISTER_REQUEST, USER_REGISTER_SUCCESS, USER_REGISTER_FAIL, USER_LOGIN_REQUEST } from '../actionTypes';

// Register User
export const userRegister = (name, email, password) => {
    return async dispatch => {
       try{
        dispatch({
            type: USER_REGISTER_REQUEST
        })
        const config = {
            headers: {'content-Type': 'application/json'}
        }
        const {data} = await axios.post("http://localhost:5000/api/user/userregister",
            {name, email, password}, config)
        dispatch({
            type: USER_REGISTER_SUCCESS,
            payload: data
        })
       }
       catch(error){
        dispatch({
            type: USER_REGISTER_FAIL,
            payload: error.response && error.response.data.message
        })
       }
    }
}

export const loginUser = (email, password) => {
    return async dispatch => {
        try{
            dispatch({
                type: USER_LOGIN_REQUEST
            })
            const config = {
                headers: {content_Type : "application/json"}
            }
            const {data} = await axios.post("http://localhost:5000/api/user/login",
                            {email, password}, config)
            dispatch({
                type: USER_REGISTER_SUCCESS,
                payload: data
            })
        }
        catch(error){
            dispatch({
                type: USER_LOGIN_FAIL,
                payload: error.response && error.response.data.message
            })
        }
    }
}
