import { USER_PROFILE_REQUEST, USER_PROFILE_SUCCESS, USER_PROFILE_FAIL } from "../../actions/actionTypes";



const userProfileReducer = (state = {}, action) => {
    switch(action.type){
        case USER_PROFILE_REQUEST:
        return{
            loading: true
        }
        case USER_PROFILE_SUCCESS:
        return{
            user: action.payload
        }
        case USER_PROFILE_FAIL:
        return{
            loading: false,
            error: action.payload
        }
    }
}

export {userProfileReducer}
