import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Table } from 'react-bootstrap'
import { fetchPurchase } from '../../redux/actions/purchase/purchaseAction';

function PurchaseDetails() {
    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(fetchPurchase())
    }, [dispatch])

    const purchaseList = useSelector(state => state.purchase)
    const {purchases} = purchaseList;
    console.log(purchases)
    return (
        <div>
            {purchases ? (
                <Table className = "w-75 mx-auto" striped bordered hover>
                <thead>
                    <tr> 
                        <th> Item ID</th>                      
                        <th>Item Name</th>
                        <th>Quantity</th>
                        <th>Unit Price</th>
                        <th>Total Amount</th>
                    </tr>
                </thead>
                <tbody>
                    {purchases.map((purchase) => {
                        return(
                        <tr key = {purchase._id}>   
                            <td>{purchase._id}</td>                 
                            <td>{purchase.itemName}</td>
                            <td>{purchase.quantity}</td>
                            <td>{purchase.unitPrice}</td>
                            <td>{purchase.quantity * purchase.unitPrice}</td>
                        </tr>
                        )                   
                    })}                    
                </tbody>
            </Table>
            ) : (
                "No Items Purchased"
            ) }          

        </div>
    )
}

export default PurchaseDetails
