import axios from "axios";
import { PURCHASE_ADDITEM_REQUEST, PURCHASE_ADDITEM_SUCCESS, PURCHASE_ADDITEM_FAIL, PURCHASE_FETCH_REQUEST, PURCHASE_FETCH_SUCCESS, PURCHASE_FETCH_FAIL } from "../actionTypes";

// Create Item
export const createItem = (itemName, quantity, unitPrice, unitCost, itemId) => {
    return async dispatch => {
        try{
            dispatch({
                type: PURCHASE_ADDITEM_REQUEST,
            })
            const config = {
                headers: {'Content-Type': 'application/json'}
            }
            const {data} = await axios.post('http://localhost:5000/api/purchase/additem', 
                            {itemName, quantity, unitPrice, unitCost, itemId},
                        config)
            dispatch({
                type: PURCHASE_ADDITEM_SUCCESS,
                payload: data
            })
        }
        catch(error){
            dispatch({
                type: PURCHASE_ADDITEM_FAIL,
                payload: error.response && error.response.data.message  
            })
        }
    }
}

export const fetchPurchase = () => {
    return async dispatch => {
        try{
            dispatch({
                type: PURCHASE_FETCH_REQUEST
            })
            const config = {
                headers: {'Content-Type': 'application/json'}
            }
            const {data} = await axios.get('http://localhost:5000/api/purchase/allitems', config)
            dispatch({
                type: PURCHASE_FETCH_SUCCESS,
                payload: data
            })
        }
        catch(error){
            dispatch({
                type: PURCHASE_FETCH_FAIL,
                payload: error.response && error.response.data.message  
            })
        }
    }
}
