import React, { useEffect } from 'react'
import { Table } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import { fetchBill } from '../../redux/actions/bill/billAction'

function Bill() {
    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(fetchBill())
    }, [dispatch])
    const billList = useSelector(state => state.bill)
    const { salesList } = billList
    console.log(salesList)
    var sum = salesList && salesList.map(item => item.total).reduce((a, b) => a + b, 0)

    


    return (
        <div>
            {salesList ? (
                <Table className="w-75 mx-auto" striped bordered hover>
                    <thead>
                        <tr>
                            <th >Item Name</th>
                            <th>Quantity</th>
                            <th>Unit Price</th>
                            <th>Total Amount</th>
                        </tr>
                    </thead>
                    <tbody>
                        {salesList && salesList.map((item) => {
                            return (
                                <tr >
                                    <td>{item.itemName}</td>
                                    <td>{item.quantity}</td>
                                    <td>{item.unitPriceSales}</td>
                                    <td>{item.total}</td>
                                </tr>
                            )
                        })}
                        <tr>
                            <td colSpan="2" >Total</td>
                            <td colSpan="2">{sum}</td>
                        </tr>
                    </tbody>
                </Table>
            ) :
                (
                    "Nothing to Bill"
                )}

        </div>
    )
}

export default Bill
