import { PURCHASE_ADDITEM_REQUEST, PURCHASE_ADDITEM_SUCCESS, PURCHASE_ADDITEM_FAIL } from "../../actions/actionTypes";


export const purchaseReducer = (state = {}, action) => {
    switch(action.type){
        case PURCHASE_ADDITEM_REQUEST:
        return {
            loading: true
        }
        case PURCHASE_ADDITEM_SUCCESS:
        return {
            purchase: action.payload
        }
        case PURCHASE_ADDITEM_FAIL:
        return{
            loading: false,
            error: action.payload
        }
    }
}
