import { CREATE_BOOK_REQUEST, 
    CREATE_BOOK_SUCCESS, 
    CREATE_BOOK_FAIL, 
    FETCH_BOOK_REQUEST, 
    FETCH_BOOK_SUCCESS, 
    FETCH_BOOK_FAIL, 
DELETE_BOOK_REQUEST,
DELETE_BOOK_SUCCESS,
DELETE_BOOK_FAIL } from "../actionTypes";
import axios from "axios";

const createBookAction = (category, author, title) => {
    return async (dispatch) => {
        try{
            dispatch ({
                type: CREATE_BOOK_REQUEST
            })
            const config = {
                'Content-Type': 'application/json'
            }
            const {data} = await axios.post('/api/books', {category, author, title}, config)
    
            dispatch({
                type:CREATE_BOOK_SUCCESS,
                payload: data
            })
        }
        catch (error){
            dispatch({
                type: CREATE_BOOK_FAIL,
                payload: error.response && error.response.data.message
            })
        }
    }
}


const fetchBooks = () => {
    return async dispatch => {
        try{
            dispatch({
                type: FETCH_BOOK_REQUEST
            })
            const config = {
                'Content-Type': 'application/json'
            }
            const {data} = await axios.get('/api/books', config)
            dispatch({
                type: FETCH_BOOK_SUCCESS,
                payload: data
            })

        }
        catch(error){
            dispatch({
                type:FETCH_BOOK_FAIL,
                payload: error.response && error.response.data.message
            })
        }
    }    
}

const deleteBook = id => {
    return async dispatch => {
      try {
        dispatch({
          type: DELETE_BOOK_REQUEST,
          loading: true,
        });
  
        const config = {
          headers: {
            'Content-Type': 'application/json',
          },
        };
        const { data } = await axios.delete(`/api/books/${id}`, config);
        dispatch({
          type: DELETE_BOOK_SUCCESS,
          payload: data,
        });
  
        dispatch({
          type: FETCH_BOOK_SUCCESS,
        });
      } catch (error) {
        dispatch({
          type: DELETE_BOOK_FAIL,
          loading: false,
          error: error.response && error.response.data.message,
        });
      }
    };
  };
  

export {createBookAction, fetchBooks, deleteBook}
