import React, { useEffect } from 'react'
import { Table } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import { fetchBill } from '../../redux/actions/bill/billAction'

function Bill() {
    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(fetchBill())
    }, [dispatch])
    const billList = useSelector(state => state.bill)
    const { salesList } = billList
    console.log(salesList)
    const total = salesList && salesList.map((item) => {
        return
        item.bills.reduce((total, bill) => {
            return total + (bill.unitPrice * item.quantity)
        }, 0)
    })



    return (
        <div>

            {salesList ? (
                <Table className="w-75 mx-auto" striped bordered hover>
                    <thead>
                        <tr>
                            <th >Item Name</th>
                            <th>Quantity</th>
                            <th>Unit Price</th>
                            <th>Total Amount</th>
                        </tr>
                    </thead>
                    <tbody>
                        {salesList && salesList.map((item) => {
                            return (
                                <tr >
                                    {item.bills.map((bill) => {
                                        return (
                                            <>
                                                <td>{bill.itemName}</td>
                                                <td>{item.quantity}</td>
                                                <td>{bill.unitPrice}</td>
                                                <td>{item.quantity * bill.unitPrice}</td>
                                            </>
                                        )
                                    })}
                                </tr>
                            )
                        })}
                        <tr>
                            <td>Total</td>
                            <td>{total}</td>
                        </tr>
                    </tbody>
                </Table>
            ) :
                (
                    "Nothing to Bill"
                )}

        </div>
    )
}

export default Bill
