import { PURCHASE_ADDITEM_REQUEST, PURCHASE_ADDITEM_SUCCESS, PURCHASE_ADDITEM_FAIL, PURCHASE_FETCH_REQUEST, PURCHASE_FETCH_SUCCESS, PURCHASE_FETCH_FAIL } from "../../actions/actionTypes";


export const purchaseReducer = (state = {}, action) => {
    switch(action.type){
        case PURCHASE_ADDITEM_REQUEST:
        return {
            loading: true
        }
        case PURCHASE_ADDITEM_SUCCESS:
        return {
            purchases: action.payload
        }
        case PURCHASE_ADDITEM_FAIL:
        return{
            loading: false,
            error: action.payload
        }
        case PURCHASE_FETCH_REQUEST:
        return {
            loading: true
        }
        case PURCHASE_FETCH_SUCCESS:
        return{
            purchases: action.payload
        }
        case PURCHASE_FETCH_FAIL:
        return{
            loading:false,
            error: action.payload
        }
        default:
        return state
    }
}
