import React, { useEffect } from 'react';
import './Profile.css';
import pic from '../../assets/img/bookpic.jpg';
import {useDispatch, useSelector} from 'react-redux'
import { Link } from 'react-router-dom';
import { getUserProfileAction } from '../../redux/actions/users/usersAction';

const Profile = ({ history }) => {
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getUserProfileAction())
    },[dispatch])

    const userProfile = useSelector(state => state.userProfile);
    const {user, loading, error} = userProfile;

  return (
    <>
    {error && <h2> {error} </h2>}
    {loading ? <h3> Loading </h3> : 
        <div className='container'>
        <div className='row'>
          <div className='col mt-5'>
            <div className='card m-auto ' style={{ width: '50%' }}>
              <img src={pic} className='card-img-top' alt='...' />
              <div className='card-body'>
                <h5 className='card-title'>{userProfile.email}</h5>
                <p className='card-text'>userProfile.name</p>
                <Link to='/user-update' className='btn btn-primary'>
                  Update your profile
                </Link>
              </div>
            </div>
          </div>
        </div>
      </div>
    }
      
      {/* Table */}
      {loading ? <h2> Loading Please Wait </h2> : 
    <table className='table table-hover'>
    <thead>
      <tr>
        <th scope='col'>Author</th>
        <th scope='col'>Book Name</th>
        <th scope='col'>Delete</th>
        <th scope='col'>Update</th>
      </tr>
    </thead>
    <tbody>
      <tr className='table-dark'>
        <th scope='row'>author</th>
        <td>Title</td>
        <td>Delete</td>
        <td>Update</td>
      </tr>
    </tbody>
  </table>
    }
    </>
  );
};

export default Profile;
