import {createStore, combineReducers, applyMiddleware} from "redux"
import thunk from "redux-thunk"
import {composeWithDevTools} from "redux-devtools-extension"
import { createBookReducer } from "../reducers/book/createBookReducer";
import { bookListReducer } from "../reducers/book/bookListReducer";
import { userReducer } from "../reducers/users/userAuthReducer";


const middlewares = [thunk]

const reducer = combineReducers({
    bookCreated: createBookReducer,
    booksList: bookListReducer,
    userLogin: userReducer
})
// Get User from localstorage and save it in Store
const userAuthFromStorage = localStorage.getItem('userAuthdata')
        ? JSON.parse(localStorage.getItem('userAuthdata')) : null   

const initialState = {
    userLogin: {userInfo: userAuthFromStorage}
}

const store = createStore(
    reducer, 
    composeWithDevTools(applyMiddleware(...middlewares))
)


export {store}
