import React, { useEffect } from 'react'
import { Table } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import { deleteBill } from '../../redux/actions/bill/billAction'
import { fetchUserBill } from '../../redux/actions/user/userAction';


function Bill() {
    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(fetchUserBill())
    }, [dispatch])
   
    const userProfile = useSelector(state => state.user)
    const {users} = userProfile
    console.log(users)
    const bill = userProfile.users && userProfile.users.bill
    console.log(bill)


    //var sum = salesList && salesList.map(item => item.total).reduce((a, b) => a + b, 0)

    const handlerBillDelete = (id) => {
        dispatch(deleteBill(id));
    }

    const print = () => {
        window.print();
    }



    return (
        <div>
            {salesList ? (
                <>
                           
                <Table className="w-75 mx-auto" striped bordered hover>
                    <thead>                                          
                        <tr>
                            <th >Item Name</th>
                            <th>Quantity</th>
                            <th>Unit Price</th>
                            <th>Total Amount</th>
                            <th>Delete</th>
                        </tr>
                    </thead>
                    <tbody>
                        {salesList && salesList.map((item) => {
                            return (
                                <tr key = {item._id} >
                                    <td>{item.itemName}</td>
                                    <td>{item.quantity}</td>
                                    <td>{item.unitPrice}</td>
                                    <td>{item.total}</td>
                                    <td><button onClick = {() => handlerBillDelete(item._id)}>Delete</button></td>
                                </tr>
                            )
                        })}
                        {/* <tr>
                            <td colSpan="2" >Total</td>
                            <td colSpan="2">{sum}</td>
                        </tr> */}
                    </tbody>
                </Table>
                </>
            ) :
                (
                    "Nothing to Bill"
                )}
                <button onClick = {print} style = {{float:"right"}}>Print</button>
              
        </div>
    )
}

export default Bill
