        config)
        dispatch({
            type: USER_REGISTER_SUCCESS,
            payload: data
        })

        // Save User to LocalStorage
        localStorage.setItem('userAuthData', JSON.stringify(data));
       }
       catch(error){
           dispatch({
               type: USER_REGISTER_FAIL,
               payload: error.response && error.response.data.message
           })
       }
    }
}

