import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Table } from 'react-bootstrap'
import {Link} from 'react-router-dom'
import {  deletePurchase } from '../../redux/actions/purchase/purchaseAction';
import { fetchUsersProfile } from '../../redux/actions/user/userAction';
import { fetchBill } from '../../redux/actions/bill/billAction';


function PurchaseDetails() {
    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(fetchUsersProfile())
    }, [dispatch])
    
    useEffect(() => {
        dispatch(fetchBill())
    }, [dispatch])
  
    const userProfile = useSelector(state => state.user)
    const {users} = userProfile
    console.log(users)

    const purchase = userProfile.users && userProfile.users.purchase
    console.log(purchase) 
   
    const billList = useSelector(state => state.bill)
    const { salesList } = billList
    console.log(salesList.quantity)

    
   
    const handlerPurchaseDelete = (id) => {
        dispatch(deletePurchase(id))
    }
    
    return (
        <div>
            {purchase ? (
                <Table className = "w-75 mx-auto pt-2"  striped bordered hover>
                <thead>
                    <tr>                                          
                        <th>Item Name</th>
                        <th>Product Key</th>
                        <th>Quantity Purchased</th>
                        <th> Quantities Available </th>
                        <th>Unit Cost </th>   
                        <th>Unit Price</th>                                           
                        <th>Edit</th>
                        <th>Delete</th>
                    </tr>
                </thead>
                <tbody>
                   {purchase.map((item) => {
                       return(                       
                           <tr>
                               <td>{item.itemName}</td>
                               <td>{item.productKey}</td>
                               <td>{item.quantity}</td>
                               <td>Wait</td>
                               <td>{item.unitCost}</td>
                               <td>{item.unitPrice}</td>
                               <td><Link to={"/" + item._id} className="btn btn-primary">Edit</Link>                           
                            </td>
                            <td><button onClick = {() => handlerPurchaseDelete(item._id)}  style={{ color: 'red', cursor: 'progress' }}>Delete</button></td>                               
                            </tr>
                       )})}        
            </tbody>
            </Table>
            ) : (
                "No Items Purchased"
            ) }                

        </div>  
        
   
    )
}

export default PurchaseDetails
