import React, {useState, useEffect} from 'react';
import {useDispatch, useSelector} from "react-redux"
import { LoginUserAction } from '../../redux/actions/users/usersAction';

const LoginUser = ({history}) => {
   const dispatch = useDispatch();
  const[email, setEmail] = useState(" ")
  const[password, setPassword] = useState(" ")

  // Grab data from store to display loading, userInfo and error
  const state = useSelector((state) => {
   return state.userLogin
  })

  const {loading, userInfo, error} = state;


  const loginUserSubmitHandler = (e) => {
    e.preventDefault();
    dispatch(LoginUserAction(email, password)); 
  }

  useEffect(() => {
    if(userInfo){
      history.push('/profile')
    }
    },[userInfo])

  return (
    <div className='row container-height'>
      <div className='col-lg-6 col-md-6 m-auto'>
        <div className='container'>
        {loading && <h1> Loading </h1>}
        {error && <p> {error} </p>}
          <form onSubmit = {loginUserSubmitHandler} >
            <fieldset>
              <div className='form-group'>
                <label htmlFor='exampleInputEmail1'>Email address</label>
                <input
                value = {email}
                onChange = {(e) => setEmail(e.target.value)}
                  type='email'
                  className='form-control'
                  id='exampleInputEmail1'
                  aria-describedby='emailHelp'
                  placeholder='Enter email'
                />
              </div>
              <div className='form-group'>
                <label htmlFor='exampleInputPassword1'>Password</label>
                <input
                value = {password}
                onChange = { (e) => setPassword(e.target.value)}
                  type='password'
                  className='form-control'
                  id='exampleInputPassword1'
                  placeholder='Password'
                />
              </div>
              <button type='submit' className='btn btn-info m-auto'>
                Login
              </button>
            </fieldset>
          </form>
        </div>
      </div>
    </div>
  );
};

export default LoginUser
