import React, {useState, useEffect} from 'react'
import { Form, Button} from 'react-bootstrap'
import { useDispatch,useSelector } from 'react-redux'
import { createBill } from '../../redux/actions/bill/billAction'
import {fetchPurchase} from '../../redux/actions/purchase/purchaseAction'




function AddBill() {
    
    const [quantity, setQuantity] = useState(0)
    useEffect(() => {
        dispatch(fetchPurchase())
    }, [dispatch])


    const dispatch = useDispatch();

    const purchaseList = useSelector(state => state.purchase)
    const {purchases} = purchaseList
    console.log(purchases)

    const formSubmitHandler = (e) => {
        e.preventDefault();
        dispatch(createBill( quantity))
       
        setQuantity(0)  
    }

    return (
        <div>
            <Form className="w-50 mx-auto" onSubmit={formSubmitHandler} >
               
                <Form.Group className="mb-2 " controlId="formBasicEmail">
                    <Form.Label>Quantity</Form.Label>
                    <Form.Control
                        type="number"
                        size="sm"
                        placeholder="Quantity"
                        value={quantity}
                        onChange={(e) => setQuantity(e.target.value)} />
                </Form.Group>

                <Button variant="primary" type="submit">
                    Submit
                </Button>                
            </Form>             
        </div>
    )
}

export default AddBill
