import axios from 'axios'
import { ADD_SALESITEM_REQUEST, ADD_SALESITEM_SUCCESS, ADD_SALESITEM_FAIL, BILL_FETCH_REQUEST, BILL_FETCH_SUCCESS, BILL_FETCH_FAIL } from '../actionTypes';


// Adding bill item
export const createBill = (productKey, quantity) => {
    return async dispatch => {
        try {
            dispatch({
                type: ADD_SALESITEM_REQUEST
            })
            const config = {
                headers: { 'Content-Type': 'application/json' }
            }
            const { data } = await axios.post("http://localhost:5000/api/bill",
                { productKey, quantity },
                config)
            dispatch({
                type: ADD_SALESITEM_SUCCESS,
                payload: data
            })
        }
        catch (error) {
            dispatch({
                type: ADD_SALESITEM_FAIL,
                payload: error.response && error.response.data.message 
            })

        }
    }

}

// Fetching bill
export const fetchBill = () => {
    return async dispatch => {
        dispatch({
            type: BILL_FETCH_REQUEST
        })
        const config = {
            headers: {'Content-Type': 'application/json'}
        }
        const {data} = await axios.get("http://localhost:5000/api/bill/salesList", config)
        dispatch({
            type: BILL_FETCH_SUCCESS,
            payload: data
        })
        dispatch({
            type: BILL_FETCH_FAIL,
            payload: error.response && error.response.data.message
        })
    }
}
