import { USER_REGISTER_REQUEST, USER_REGISTER_SUCCESS, USER_REGISTER_FAIL, USER_LOGIN_REQUEST, USER_LOGIN_SUCCESS, USER_LOGIN_FAIL } from "../../actions/actionTypes";

const userReducer = (state={}, action) => {
    switch(action.type){
        case USER_REGISTER_REQUEST: 
        return{
            loading: true
        }
        case USER_REGISTER_SUCCESS:
        return{
            userInfo: action.payload
        }
        case USER_REGISTER_FAIL:
        return{
            error: action.payload,
            loading: false
        }
        case USER_LOGIN_REQUEST:
        return{
            loading: true
        }
        case USER_LOGIN_SUCCESS:
        return{
            userInfo: action.payload
        }
        case USER_LOGIN_FAIL:
        return{
            error: action.payload,
            loading: false
        }
        default:
        return state
    }
}

export {userReducer}
