import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Table } from 'react-bootstrap'
import {Link} from 'react-router-dom'
import { deletePurchase } from '../../redux/actions/purchase/purchaseAction';
import { fetchUsers } from '../../redux/actions/user/userAction';


function PurchaseDetails() {
    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(fetchUsers())
    }, [dispatch])

    const userPurchaseList = useSelector(state => state.user)
    const {users} = userPurchaseList;
    console.log(users)

    const handlerPurchaseDelete = (id) => {
        dispatch(deletePurchase(id))
    }
    
    return (
        <div>
            {users ? (
                <Table className = "w-75 mx-auto pt-2"  striped bordered hover>
                <thead>
                    <tr>                                          
                        <th>Item Name</th>
                        <th>Product Key</th>
                        <th>Quantity Purchased</th>
                        <th> Quantities Available </th>
                        <th>Unit Price</th>
                        <th>Unit Cost </th>                      
                        <th>Edit</th>
                        <th>Delete</th>
                    </tr>
                </thead>
                <tbody>
                    {users && users.map((user) => {
                        return(
                        <tr key = {user._id}>              
                            <td>{user.itemName}</td>
                            <td>{user.productKey}</td>
                            <td>{user.quantity}</td>
                            <td>{user.quantity - user.salesQty}</td>
                            <td>{user.unitCost}</td>
                            <td>{user.unitPrice}</td>            
                            <td>                                                                
                            <Link to={"/" + user._id} className="btn btn-primary">Edit</Link>                           
                            </td>
                            <td><button onClick = {() => handlerPurchaseDelete(user._id)}  style={{ color: 'red', cursor: 'progress' }}>Delete</button></td>
                        </tr>
                        )                   
                    })}                    
                </tbody>
            </Table>
            ) : (
                "No Items Purchased"
            ) }                

        </div>
    )
}

export default PurchaseDetails
