import React, { useState, useEffect } from 'react'
import { Form, Button, Table } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import { createBill, fetchBill } from '../../redux/actions/bill/billAction'



function AddBill() {
    const [productKey, setProductKey] = useState(" ")
    const [quantity, setQuantity] = useState(0)

    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(fetchBill())
    }, [dispatch])

    const billList = useSelector(state => state.bill)
    const { salesList } = billList    
    console.log(salesList) 


    const formSubmitHandler = (e) => {
        e.preventDefault();
        dispatch(createBill(productKey, quantity))
        setProductKey(" ")
        setQuantity(0)
    }

    return (
        <div>
            <Form className="w-50 mx-auto" onSubmit={formSubmitHandler} >
                <Form.Group className="mb-2 " controlId="formBasicEmail">
                    <Form.Label>Product Key</Form.Label>
                    <Form.Control
                        type="text"
                        size="sm"
                        placeholder="Enter the Product Key"
                        value={productKey}
                        onChange={(e) => setProductKey(e.target.value)} />
                </Form.Group>

                <Form.Group className="mb-2 " controlId="formBasicEmail">
                    <Form.Label>Quantity</Form.Label>
                    <Form.Control
                        type="number"
                        size="sm"
                        placeholder="Quantity"
                        value={quantity}
                        onChange={(e) => setQuantity(e.target.value)} />
                </Form.Group>

                <Button variant="primary" type="submit">
                    Submit
                </Button>
                <Table className="w-75 mx-auto" striped bordered hover>
                <thead>
                    <tr>
                        <th>Item Name</th>
                        <th>Quantity</th>
                        <th>Unit Price</th>
                        <th>Total Amount</th>
                    </tr>
                </thead>
                <tbody>
                    {salesList && salesList.map(item => {
                        return (
                            item.bills.map((bill) => {
                                return (                                    
                                    <tr>
                                        <td>bill.itemName</td>
                                        <td>{quantity}</td>
                                        <td>bill.unitPrice</td>
                                        <td>{quantity * bill.unitPrice}</td>
                                    </tr>
                                )
                            })
                        )
                    })}

                </tbody>
            </Table>
            </Form>          
        </div>
    )
}

export default AddBill
