import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Table } from 'react-bootstrap'
import { fetchPurchase } from '../../redux/actions/purchase/purchaseAction';

function PurchaseDetails() {
    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(fetchPurchase)
    }, [dispatch])

    const purchaseList = useSelector(state => state.purchase)
    const { loading, error, purchase } = purchaseList;
    return (
        <div>
            {loading ? (
        <Table className = "w-75 mx-auto" striped bordered hover>
        <thead>
            <tr>
                <th>#</th>
                <th>Item Name</th>
                <th>Quantity</th>
                <th>Unit Price</th>
            </tr>
        </thead>
        <tbody>
            {purchase && 
            purchase.map(purchase => {
                return(
                    <tr key = {purchase._id}>                       
                    <td>{purchase.itemName}</td>
                    <td>{purchase.quantity}</td>
                    <td>{purchase.unitPrice}</td>
                </tr>
                )                   
            })}
            
        </tbody>
    </Table>    
        ) : (
            {error}
        )}
            

        </div>
    )
}

export default PurchaseDetails
