import React, {useState, useEffect} from 'react'
import {useParams} from 'react-router-dom'
import {useDispatch, useSelector} from 'react-redux'
import {fetchPurchase, updatePurchase} from '../../redux/actions/purchase/purchaseAction'

function EditPurchase() {
    const {id} = useParams();
    // Getting the purchase details from the store
    const purchaseList = useSelector(state => state.purchase)
    const {purchases, loading} = purchaseList;

    const [itemName, setItemName] = useState(purchases && !loading && purchases.itemName)
    const [productKey, setProductKey] = useState(purchases && !loading && purchases.productKey)
    const [quantity, setQuantity] = useState(purchases && !loading && purchases.quantity)
    const [unitCost, setUnitCost] = useState(purchases && !loading && purchases.unitCost)
    const [unitPrice, setunitPrice] = useState(purchases && !loading && purchases.unitPrice)
    
    return (
        <div>
            Edit Purchase
        </div>
    )
}

export default EditPurchase
