import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Table } from 'react-bootstrap'
import {Link} from 'react-router-dom'
import {  deletePurchase } from '../../redux/actions/purchase/purchaseAction';
import { fetchUsersProfile } from '../../redux/actions/user/userAction';


function PurchaseDetails() {
    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(fetchUsersProfile())
    }, [dispatch])

    const userProfile = useSelector(state => state.user)
    const {users} = userProfile;
    console.log(users)
    const purchase = userProfile.users && userProfile.users.purchase
    console.log(purchase)
    
   
    const handlerPurchaseDelete = (id) => {
        dispatch(deletePurchase(id))
    }
    
    return (
        <div>
            {users ? (
                <Table className = "w-75 mx-auto pt-2"  striped bordered hover>
                <thead>
                    <tr>                                          
                        <th>Item Name</th>
                        <th>Product Key</th>
                        <th>Quantity Purchased</th>
                        <th> Quantities Available </th>
                        <th>Unit Cost </th>   
                        <th>Unit Price</th>                                           
                        <th>Edit</th>
                        <th>Delete</th>
                    </tr>
                </thead>
                <tbody>
                   {users.map((item) => {
                       return(
                       item.purchase.map((items) => {
                           return(
                           <tr>
                               <td>{items.itemName}</td>
                               <td>{items.productKey}</td>
                               <td>{items.quantity}</td>
                               <td>Wait</td>
                               <td>{items.unitCost}</td>
                               <td>{items.unitPrice}</td>
                               <td><Link to={"/" + items._id} className="btn btn-primary">Edit</Link>                           
                            </td>
                            <td><button onClick = {() => handlerPurchaseDelete(items._id)}  style={{ color: 'red', cursor: 'progress' }}>Delete</button></td>                               
                           </tr>
                       )})
                   )})}                     
            </tbody>
            </Table>
            ) : (
                "No Items Purchased"
            ) }  

               

        </div>
    )
}

export default PurchaseDetails
