import React, { useEffect } from 'react'
import { Table } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import { fetchBill } from '../../redux/actions/bill/billAction'

function Bill() {
    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(fetchBill())
    }, [dispatch])
    const billList = useSelector(state => state.bill)
    const { salesList } = billList
    console.log(salesList)
    return (
        <div>
            {salesList ? (
                <Table className="w-75 mx-auto" striped bordered hover>
                    <thead>
                        <tr>
                            <th>Item Name</th>
                            {/* <th>Quantity</th>
                            <th>Unit Price</th>
                            <th>Total Amount</th> */}
                        </tr>
                    </thead>
                    <tbody>
                        {/* {salesList && salesList.map(item => {
                            return (
                                item.bills.map((bill) => {
                                    return (
                                        <tr key={item.productKey}>
                                            <td>{bill.itemName}</td>
                                            <td>{item.quantity}</td>
                                            <td>{bill.unitPrice}</td>
                                            <td>{item.quantity * bill.unitPrice}</td>
                                        </tr>
                                    )
                                })
                            )
                        })} */}
                        {salesList.map((item) => (
                            <tr key={item.id}>
                                {Object.values(item).map((val) => (
                                    <td>{val.itemName}</td>
                                ))}
                            </tr>
                        ))}
                    </tbody>
                </Table>
            ) :
                (
                    "Nothing to Bill"
                )}

        </div>
    )
}

export default Bill
